import React, { useState, useEffect, useRef } from 'react';
import InputMask from "react-input-mask";
import './style.css'
import { FiTrash2 } from 'react-icons/fi'
import api from '../../services/api'
import viaCep from '../../services/viaCep'
import Logo from '../../assets/BuscaCep.png';
export default function Adress() {
    const [cep, setCep] = useState('')
    const [adress, setAdress] = useState([]);
    const [curretPage, setCurrentPage] = useState(1)
    const [hasEndingPosts, setHasEndingPosts] = useState(false);
    const loaderRef = useRef(null);
    useEffect(() => {
        let data = [];
        const handleResquest = async () => {
            data  = await api.get(`/adress/index?page=${curretPage}`)
        }

        if (!data.length) {
            console.log("Os episodios acabaram");
            return;
        }

        setAdress(...data);

        handleResquest();
    }, [curretPage])

    useEffect(() => {
        const options = {
            root: null,
            rootMargin: "20px",
            threshold: 1.0
        };
        const observer = new IntersectionObserver((entities) => {
            const target = entities[0];

            if (target.isIntersecting) {
                setCurrentPage(old => old + 1);
            }
        }, options);

        if (loaderRef.current) {
            observer.observe(loaderRef.current);
        }

    }, [])
    async function saveAdress(adress) {
        try {
            await api.post('/adress/create', adress)
        } catch (err) {
            alert(err)
        }
    }
    async function deleteAdress(id, index) {
        try {

            await api.delete(`/adress/delete/${id}`)
            setAdress(adress.filter(adress => adress.id !== id));
        } catch (err) {
            console.log(err)
        }
    }
    async function buscaCep(cep) {
        try {
            const formatCep = Number(cep.replace(/[^0-9]/g, ''))
            const adress = await viaCep.get(`${formatCep}/json/`)
            await saveAdress(adress)
            setAdress(adress);
            console.log(adress)
        } catch (err) {
            console.log(err)
            alert("error")
        }
    }
    return (
        <div className="adress-conteiner">
            <header>
                <img src={Logo} alt="logo" />
                <form onSubmit={() => buscaCep(cep)} className="form">
                    <label>CEP:</label>
                    <InputMask mask="99999-999" placeholder="XXXXX-XXX" value={cep}
                        onChange={e => setCep(e.target.value)}
                        type="text"
                    />
                    <button className="button" type='submit'>Pesquisar</button>
                </form>
            </header>
            <h1>Endereços Pesquisados</h1>
            <ul>
                {adress?.map((adress, index) => (
                    <li key={adress.id}>
                        <strong>Cep:</strong>
                        <p>{adress.cep}</p>

                        <strong>Bairro:</strong>
                        <p>{adress.district}</p>

                        <strong>Municipio:</strong>
                        <p>{adress.municipality}</p>

                        <strong>Rua:</strong>
                        <p>{adress.public_place}</p>

                        <strong>Estado:</strong>
                        <p>{adress.states}</p>
                        <button onClick={() => deleteAdress(adress.id, index)} type="button">
                            <FiTrash2 size={18} color="#a8a8b3" />
                        </button>
                    </li>
                )
                )}
            </ul>
            <p ref={loaderRef}>Carregando mais episodios...</p>
        </div>
    );
}
