import React,{useState} from 'react';
import './styles.css'
import api from '../../services/api';
import {Link}from 'react-router-dom';
import logo from '../../assets/logo.png'
import {FiArrowLeft} from 'react-icons/fi';
export default function Register(){
  const [name,setName]=useState("");
  const [email,setEmail]=useState("");
  const [whatsApp,setWhatsApp]=useState("");
  const [cidade,setCidade]=useState("");
  const [Uf,setUf]=useState("");
 async function Headle(e){
    e.preventDefault();
    const date={
      name,
      email,
      whatsApp,
      cidade,
      Uf
    };
    try{
    const response=await api.post('/ongs',date)
    alert(`seu Id de acesso ${response.date.id}`);
    }catch(err){
      alert("error no cadstro")
    }

  }
  
  return(
      <div className="registre-conteiner" >
       <div className='content' >
         <section>
             <img src={logo} alt="help"/>

             <h1>Cadastro</h1>
             <p>Fa√ßa seu cadstro, entre na plataforma e ajude pessas a encontrar os casos da sua ONG</p>
         <Link className="back-link" to='/'>
           <FiArrowLeft  size={18} color='green'/>
           Nao tenho cadastro
         </Link>
         </section>
         <form  onSubmit={Headle}>
           <input placeholder="Nome da ONG"
           value={name}
           onChange={e=>setName(e.target.value)}/>
           <input type='email'placeholder="Email"
            value={email}
            onChange={e=>setEmail(e.target.value)}/>
           <input placeholder="WhatsApp"
            value={whatsApp}
            onChange={e=>setWhatsApp(e.target.value)}/>
           <div className="grup-input">
           <input placeholder="Cidade"
            value={cidade}
            onChange={e=>setCidade(e.target.value)}/>
           <input placeholder="UF" style={{width:80}}
            value={Uf}
            onChange={e=>setUf(e.target.value)}/>
           </div>
           <button className="button" type='submit'>Cadastrar</button>
         </form>
       </div>
      </div>
  );
}
