import React, { useState, useEffect } from 'react';
import InputMask from "react-input-mask";
import './style.css'
import api from '../../services/api'
import viaCep from '../../services/viaCep'
import Logo from '../../assets/BuscaCep.png';
export default function Adress() {
    const [cep, setCep] = useState('')
    const [adress, setAdress] = useState({});

    /*useEffect(()=>{
    api.get('/profile',{headers:{
        autori:idong,
    }
    }).then(response=>{
        setInsidents(response.data)
    })
    
    },[idong])
 
        const ongName=localStorage.getItem('ongName')
     function Logout(){
      localStorage.clear()
      history.push('/')
    }*/
    async function buscaCep(cep){
        try{
            const adress = await viaCep.get(`/${cep}/json/`)
            console.log(adress)
            setAdress(adress);
        }catch(err){
            if(err.statusCode == "404") console.log("Nao encontrado")
            alert("error")
        }
    }
    return (
        <div className="adress-conteiner">
            <header>
            <img src={Logo} alt="logo"/>
                <form onSubmit={()=>buscaCep(cep)} className="form">
                <label for="CEP">CEP:
                    <InputMask mask="99999-999" placeholder="XXXXX-XXX"value={cep}
                        onChange={e => setCep(e.target.value)}
                        name="CEP" id="CEP" type="text" pattern="[0-9]{5}"
                        />
                    </label>
                    <button className="button" type='submit'>Pesquisar</button>
                </form>
            </header>
            <h1>Endere√ßos Pesquisados</h1>
            <ul>

            </ul>
        </div>
    );
}
