import React, { useState } from 'react';
import InputMask from "react-input-mask";
import './style.css'
import api from '../../services/api'
import viaCep from '../../services/viaCep'
import Logo from '../../assets/BuscaCep.png';
export default function Adress() {
    const [cep, setCep] = useState('')
    const [adress, setAdress] = useState({});
    const [adressApi, setAdressApi] = useState([])
    useEffect(()=>{
    api.get('/adress/index').then(response=>{
        setInsidents(response.data)
    })
    
    },[])
    async function saveAdress(adress){
        try{
        await api.post('/adress/create', adress)
        }catch(err){
            alert(err)
        }
    }
    async function buscaCep(cep){
        try{
            const formatCep = Number(cep.replace(/[^0-9]/g, ''))
            const adress = await viaCep.get(`${formatCep}/json/`)
            setAdress(adress);
            console.log(adress)
        }catch(err){
            console.log(err)
            alert("error")
        }
    }
    return (
        <div className="adress-conteiner">
            <header>
            <img src={Logo} alt="logo"/>
                <form onSubmit={()=>buscaCep(cep)} className="form">
                <label>CEP:</label>
                    <InputMask  mask="99999-999" placeholder="XXXXX-XXX"value={cep}
                        onChange={e => setCep(e.target.value)}
                        type="text" 
                        />
                    <button className="button" type='submit'>Pesquisar</button>
                </form>
            </header>
            <h1>Endere√ßos Pesquisados</h1>
            <ul>

            </ul>
        </div>
    );
}
